trait BSeller_Core_Trait_Config
{

    /**
     * @param string   $field
     * @param string   $group
     * @param string   $section
     * @param int|null $storeId
     *
     * @return mixed
     */
    protected function getStoreConfig($field, $group, $section, $storeId = null)
    {
        $path = implode('/', array($section, $group, $field));
        return Mage::getStoreConfig($path, $storeId);
    }


    /**
     * @param string   $field
     * @param string   $group
     * @param string   $section
     * @param int|null $storeId
     *
     * @return mixed
     */
    protected function getModuleConfig($field, $group, $section = 'bseller_core', $storeId = null)
    {
        return $this->getStoreConfig($field, $group, $section, $storeId);
    }
}
