class BSeller_SkyHub_Model_Config_Customer extends BSeller_SkyHub_Model_Config
{

    /**
     * @return array
     */
    public function getSkyHubFixedAttributes()
    {
        if (empty($this->attributes)) {
            $this->attributes = (array)$this->getConfig()->getNode('skyhub/customer_attributes')->asArray();
        }

        return (array)$this->attributes;
    }

    /**
     * @return array
     */
    public function getBlacklistedAttributes()
    {
        if (empty($this->blacklist)) {
            $this->blacklist = (array) $this->getConfig()
                ->getNode('skyhub/customer/attributes/blacklist')
                ->asArray();
            $this->blacklist = array_keys($this->blacklist);
        }

        return $this->blacklist;
    }
}