class BSeller_SkyHub_Model_System_Config_Source_Customer_Attributes
    extends BSeller_Core_Model_System_Config_Source_Abstract
{
    
    use BSeller_SkyHub_Trait_Customer_Attribute;
    
    
    /**
     * @return array
     */
    protected function optionsKeyValue($multiselect = null)
    {
        $attributes = $this->getCustomerAttributesCollection();
        $options    = array();
        
        if (!$multiselect) {
            $options[''] = $this->__('-- Select One Attribute --');
        }
        
        /** @var Mage_Eav_Model_Entity_Attribute $attribute */
        foreach ($attributes as $attribute) {
            $label = "{$attribute->getFrontend()->getLabel()} [{$attribute->getAttributeCode()}]";
            $options[$attribute->getId()] = $label;
        }
        
        return $options;
    }
}
