class BSeller_SkyHub_Adminhtml_Bseller_Skyhub_Sales_Order_ImportController
    extends BSeller_SkyHub_Controller_Admin_Queue
{

    protected $_aclPrefix = 'bseller/bseller_skyhub/sales_import/';

    public function formAction()
    {
        $this->init('Import Manually');
        $this->renderLayout();
    }


    /**
     * Submit the manual order import.
     *
     * @throws Mage_Core_Model_Store_Exception
     */
    public function submitAction()
    {
        $this->prepareStore();
        
        $codes = $this->getCleanedOrderCodes();
        
        if (empty($codes)) {
            $this->redirectBack();
            return;
        }
        
        /** @var string $code */
        foreach ($codes as $code) {
            $this->importOrder($code);
        }
        
        $this->redirectBack();
    }
    
    
    /**
     * See the log.
     */
    public function logAction()
    {
        $this->init('Import Log');
        $this->renderLayout();
    }
    
    
    /**
     * Delete Unique Queue ID.
     */
    public function deleteAction()
    {
        $queueId = (int) $this->getRequest()->getParam('id');
        
        $this->deleteQueueIds($queueId);
        $this->redirectBack();
    }
    
    
    /**
     * Delete a bunch of queue IDs.
     */
    public function massDeleteAction()
    {
        $queueIds = (array) $this->getRequest()->getPost('queue_ids');
        
        $this->deleteQueueIds($queueIds);
        $this->redirectBack();
    }
    
    
    public function retryAction()
    {
        $queueId = (int) $this->getRequest()->getParam('id');
    
        if ($queueId) {
            $this->importOrderByQueueId($queueId);
        }
    
        $this->redirectBack();
    }
    
    
    /**
     * Retry to import the failed orders.
     *
     * @throws Exception
     * @throws Mage_Core_Exception
     */
    public function massRetryAction()
    {
        $queueIds = (array) $this->getRequest()->getPost('queue_ids');
        
        foreach ($queueIds as $queueId) {
            if (empty($queueId)) {
                continue;
            }
            
            $this->importOrderByQueueId($queueId);
        }
        
        $this->redirectBack();
    }


    /**
     * @return bool
     */
    protected function _isAllowed()
    {
        $action = strtolower($this->getRequest()->getActionName());
        switch ($action) {
            case 'log':
            case 'massretry':
            case 'massdelete':
            case 'retry':
            case 'delete':
                $this->_aclSuffix = 'logs';
                break;
            default:
                $this->_aclSuffix = 'manual_import';
                break;
        }

        return parent::_isAllowed();
    }

    public function exportSkyhubCsvAction()
    {
        $fileName = 'skyhub_errors.csv';
        $grid = $this->getLayout()->createBlock('bseller_skyhub/adminhtml_sales_order_import_log_export');
        $this->_prepareDownloadResponse($fileName, $grid->getCsvFile());
    }

    public function exportSkyhubExcelAction()
    {
        $fileName = 'skyhub_errors.xml';
        $grid = $this->getLayout()->createBlock('bseller_skyhub/adminhtml_sales_order_import_log_export');
        $this->_prepareDownloadResponse($fileName, $grid->getExcelFile($fileName));
    }
}
